import React from 'react';

interface ProductCardProps {
    product: {
        id: string;
        name: string;
        description: string;
        price: number;
        imageUrl: string;
    };
}

const ProductCard: React.FC<ProductCardProps> = ({ product }) => {
    const handleBuy = () => {
        // WhatsApp contact number (India)
        const phoneNumber = '919207232303';

        // Create message with product details
        const message = `Hi, I'm interested in purchasing:\n\nðŸ“¦ *${product.name}*\n\nðŸ“ Details: ${product.description}\nðŸ’° Price: $${product.price.toFixed(2)}\n\nPlease let me know the availability and how to proceed with the purchase.`;

        // Encode the message for URL
        const encodedMessage = encodeURIComponent(message);

        // Create WhatsApp link
        const whatsappLink = `https://wa.me/${phoneNumber}?text=${encodedMessage}`;

        // Open in new window
        window.open(whatsappLink, '_blank');
    };

    const handleAddToCart = () => {
        alert(`${product.name} added to cart!`);
    };

    return (
        <div className="product-card">
            <img src={product.imageUrl} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <div className="product-price">${product.price.toFixed(2)}</div>
            <div className="product-actions">
                <button className="btn-buy" onClick={handleBuy}>Buy Now</button>
                <button className="btn-cart" onClick={handleAddToCart}>Add to Cart</button>
            </div>
        </div>
    );
};

export default ProductCard;
