import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';

const HomePage: React.FC = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [currentSlide, setCurrentSlide] = useState(0);

    // Banner slideshow images
    const bannerImages = [
        {
            id: 1,
            src: 'https://via.placeholder.com/600x300?text=Premium+Crockery+1',
            alt: 'Premium Crockery 1'
        },
        {
            id: 2,
            src: 'https://via.placeholder.com/600x300?text=Premium+Crockery+2',
            alt: 'Premium Crockery 2'
        },
        {
            id: 3,
            src: 'https://via.placeholder.com/600x300?text=Premium+Crockery+3',
            alt: 'Premium Crockery 3'
        },
        {
            id: 4,
            src: 'https://via.placeholder.com/600x300?text=Premium+Crockery+4',
            alt: 'Premium Crockery 4'
        },
        {
            id: 5,
            src: 'https://via.placeholder.com/600x300?text=Premium+Crockery+5',
            alt: 'Premium Crockery 5'
        },
        {
            id: 6,
            src: 'https://via.placeholder.com/600x300?text=Premium+Crockery+6',
            alt: 'Premium Crockery 6'
        }
    ];

    // Auto-slide every 5 seconds
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentSlide((prev) => (prev + 1) % bannerImages.length);
        }, 5000);
        return () => clearInterval(timer);
    }, [bannerImages.length]);

    const nextSlide = () => {
        setCurrentSlide((prev) => (prev + 1) % bannerImages.length);
    };

    const prevSlide = () => {
        setCurrentSlide((prev) => (prev - 1 + bannerImages.length) % bannerImages.length);
    };

    const goToSlide = (index: number) => {
        setCurrentSlide(index);
    };

    // Sample products data
    const products = [
        {
            id: '1',
            name: 'Ceramic Bowl',
            description: 'Beautiful handcrafted ceramic bowl perfect for serving',
            price: 24.99,
            imageUrl: 'https://via.placeholder.com/280x250?text=Ceramic+Bowl'
        },
        {
            id: '2',
            name: 'Dinner Plate Set',
            description: 'Set of 4 elegant dinner plates',
            price: 49.99,
            imageUrl: 'https://via.placeholder.com/280x250?text=Dinner+Plate'
        },
        {
            id: '3',
            name: 'Tea Cup',
            description: 'Delicate porcelain tea cup with handle',
            price: 12.99,
            imageUrl: 'https://via.placeholder.com/280x250?text=Tea+Cup'
        },
        {
            id: '4',
            name: 'Serving Platter',
            description: 'Large serving platter for special occasions',
            price: 59.99,
            imageUrl: 'https://via.placeholder.com/280x250?text=Serving+Platter'
        },
        {
            id: '5',
            name: 'Soup Bowl',
            description: 'Deep soup bowl with beautiful glaze',
            price: 15.99,
            imageUrl: 'https://via.placeholder.com/280x250?text=Soup+Bowl'
        },
        {
            id: '6',
            name: 'Coffee Mug Set',
            description: 'Set of 2 cozy coffee mugs',
            price: 22.99,
            imageUrl: 'https://via.placeholder.com/280x250?text=Coffee+Mug'
        }
    ];

    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
    };

    return (
        <div>
            <header className="header">
                <div className="header-content">
                    <div className="header-logo">üçΩÔ∏è CrockeryShop</div>
                    <form className="search-bar" onSubmit={handleSearch}>
                        <input
                            type="text"
                            placeholder="Search products..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <button type="submit">Search</button>
                    </form>
                    <nav className="header-nav">
                        <a href="#home">Home</a>
                        <a href="#products">Products</a>
                        <a href="#cart">Cart</a>
                        <a href="#contact">Contact</a>
                    </nav>
                </div>
            </header>

            <section className="banner">
                <div className="banner-content">
                    <h2>Premium Quality Crockery</h2>
                    <p>Discover our exclusive collection of handcrafted ceramic and porcelain pieces</p>
                    <button className="banner-btn">Shop Now</button>
                </div>
                <div className="banner-image">
                    <img src="https://via.placeholder.com/600x300?text=Premium+Crockery" alt="Premium Crockery" />
                </div>
            </section>

            <section className="products-section">
                <h2>Our Premium Crockery Collection</h2>
                <div className="products-grid">
                    {filteredProducts.map(product => (
                        <ProductCard key={product.id} product={product} />
                    ))}
                </div>
            </section>
        </div>
    );
};

export default HomePage;
